var types = [];
types[200] = "success";
types[400] = "warning";
types[404] = "error";
types[999] = "error";

$(function(){

	$("#btn-login").click(function(){
		var username = $("#username").val();
		var password = $("#password").val();

		$.ajax({
			type: "POST",
			url:"system/ajax/login.php",
			dataType: "json",
			data: {"username": username, "password": password},
			success: function(rt){
				swal("Bilgi", rt.Text, types[rt.Code]);
				if (rt.Code == 200)
					  setTimeout(function(){
				   location.href = "home.php";
				}, 2000);

				
				
			}
		});
	});
	

	
	$("#btn-uye").click(function(){
			var uyeid = $("#uyeid").val();
			var usernamea = $("#usernamea").val();
			var passworda = $("#passworda").val();
			var adsoyada = $("#adsoyada").val();
			var emaila = $("#emaila").val();
			var telefona = $("#telefona").val();
			$.ajax({
				type: "POST",
				url:"system/ajax/uyeduzenle.php",
				dataType: "json",
				data: {"uyeid": uyeid,"usernamea": usernamea,"passworda": passworda,"adsoyada": adsoyada,"emaila": emaila,"telefona": telefona},
				success: function(rt){
					swal("Bilgi", rt.Text, types[rt.Code]);
					if (rt.Code == 200) {
							location.href = "uye-detay.php?uyeid="+uyeid;
						} 
				}
			});
	});
	
	$("#btn-profil").click(function(){
			var apassword = $("#apassword").val();
			var ausername = $("#ausername").val();
			$.ajax({
				type: "POST",
				url:"system/ajax/profil.php",
				dataType: "json",
				data: {"ausername": ausername,"apassword": apassword},
				success: function(rt){
					swal("Bilgi", rt.Text, types[rt.Code]);
					if (rt.Code == 200) {
							location.href = "profil.php";
						} 
				}
			});
	});
	
	$("#btn-siteayara").click(function(){
			var sitename = $("#sitename").val();
			var siteaciklama = $("#siteaciklama").val();

			$.ajax({
				type: "POST",
				url:"system/ajax/siteayara.php",
				dataType: "json",
				data: {"sitename": sitename,"siteaciklama": siteaciklama},
				success: function(rt){
					swal("Bilgi", rt.Text, types[rt.Code]);
					if (rt.Code == 200) {
							location.href = "siteayar.php";
						} 
				}
			});
	});
	
		
	$("#btn-forget").click(function(){
		var f_email = $("#forget-email").val();
		$.ajax({
			type: "POST",
			url:"ajax/forget.php",
			dataType: "json",
			data: {"email": f_email},
			success: function(rt){
				swal("Information", rt.Text, types[rt.Code]);
				if (rt.Code == 200) location.reload();
			}
		});
	});
});
