var types = [];
types[200] = "success";
types[400] = "warning";
types[404] = "error";
types[999] = "error";

$(function(){

	$("#btn-login").click(function(){
		var username = $("#username").val();
		var password = $("#password").val();

		$.ajax({
			type: "POST",
			url:"system/ajax/login.php",
			dataType: "json",
			data: {"username": username, "password": password},
			success: function(rt){
				swal("Bilgi", rt.Text, types[rt.Code]);
				if (rt.Code == 200)
					  setTimeout(function(){
				   location.href = "home.php";
				}, 2000);

				
				
			}
		});
	});
	

	
	$("#btn-profil").click(function(){
			var apassword = $("#apassword").val();

			$.ajax({
				type: "POST",
				url:"system/ajax/profil.php",
				dataType: "json",
				data: {"apassword": apassword},
				success: function(rt){
					swal("Bilgi", rt.Text, types[rt.Code]);
					if (rt.Code == 200) {
							location.href = "profil.php";
						} 
				}
			});
	});
		$("#btn-soru").click(function(){
			var soru = $("#soru").val();

			$.ajax({
				type: "POST",
				url:"system/ajax/soru.php",
				dataType: "json",
				data: {"soru": soru},
				success: function(rt){
					swal("Bilgi", rt.Text, types[rt.Code]);
					if (rt.Code == 200)
					  setTimeout(function(){
				   location.href = "sorusor.php";
				}, 2000);
				}
			});
	});
	$("#btn-register").click(function(){
	
		var adsoyad = $("#adsoyad").val();	
		var telefon = $("#telefon").val();
		var email = $("#email").val();
		var username = $("#username").val();
		var password = $("#password").val();
	
		$.ajax({
			type: "POST",
			url:"system/ajax/register.php",
			dataType: "json",
			data: {"adsoyad": adsoyad,"telefon": telefon,"email": email,"username": username,"password": password},
			success: function(rt){
				swal("Bilgi", rt.Text, types[rt.Code]);
				if (rt.Code == 200) {
					setTimeout(function(){
					location.href = "index.php";
					},1000);
				}
			}
		});
	});
	
	$("#btn-forget").click(function(){
		var f_email = $("#forget-email").val();
		$.ajax({
			type: "POST",
			url:"ajax/forget.php",
			dataType: "json",
			data: {"email": f_email},
			success: function(rt){
				swal("Information", rt.Text, types[rt.Code]);
				if (rt.Code == 200) location.reload();
			}
		});
	});
});
